import React, {useContext, useEffect, useState} from 'react'
import './style.css'
import {Redirect} from "react-router-dom";
import {CurrentUserContext} from "contexts/currentUser";
import firebase from "../../../firebase";


const Dashboard = () => {
    const [currentUserState] = useContext(CurrentUserContext);
    const [tableData, setTableData] = useState([]);

    useEffect(() => {
        if (currentUserState.isLoggedIn === null) {
            return; //null
        }

        if (currentUserState.isLoggedIn === false) {
            return <Redirect to="/"/>
        }
        firebase.getArticles().then(snapshot => {
            let midArr = [];
            snapshot.forEach(doc => {
              midArr.push(doc.data());
            });
            setTableData(midArr);
        })
            .catch(error => {
                alert(error);
            });

    }, [currentUserState]);

    return (<div>
            <table>
                <caption>A summary of the UK's most famous punk bands</caption>
                <thead>
                <tr>
                    <th scope="col">Title</th>
                    <th scope="col">Tags</th>
                </tr>
                </thead>
                <tbody>
                {tableData.length ? tableData.map(doc => (
                        <tr key={doc.title}>
                            <th scope="row">{doc.title}</th>
                            <td>{doc.tagList.map(tag => (<span key={tag}>{tag}&nbsp;</span>))}</td>
                        </tr>
                    )) :
                    <tr>
                        <td></td>
                        <td></td>
                    </tr>
                }
                </tbody>
                <tfoot>
                <tr>
                    <th scope="row" colSpan="2">Total albums</th>
                    <td colSpan="2">{tableData.length}</td>
                </tr>
                </tfoot>
            </table>
        </div>
    );
}
export default Dashboard;