import React, {createContext, useReducer} from 'react'

const initialState = {
    isLoading: false,
    articles: []
};

const reducer = (state, action) => {
    switch (action.type) {
        case 'LOADING':
            return {...state, isLoading: true};
        case 'ADD_ARTICLE':
            return {...state, articles: action.payload, isLoading: false};
        case 'EDIT_ARTICLE':
            return {...state, articles: action.payload, isLoading: false};
        case 'GET_ARTICLES':
            return {...state, articles: action.payload, isLoading: false};
        case 'DELETE_ARTICLE':
            return {...state, articles: action.payload, isLoading: false};
        default:
            return state
    }
};

export const ArticlesDataContext = createContext()

export const ArticlesDataProvider = ({children}) => {
    const value = useReducer(reducer, initialState)

    return (
        <ArticlesDataContext.Provider value={value}>
            {children}
        </ArticlesDataContext.Provider>
    )
};
